package x402

import (
	"errors"
	"fmt"

	"github.com/ethereum/go-ethereum/accounts/abi"
	"github.com/ethereum/go-ethereum/common"
)

// ERC-6492 Universal Signature Validator constants

const (
	// bytes32(uint256(keccak256("erc6492.invalid.signature")) - 1).
	erc6492MagicValue = "0x6492649264926492649264926492649264926492649264926492649264926492"
)

// erc6492ByteCode is the bytecode of the Universal Signature Validator contract.
// This contract can verify signatures from EOA accounts, deployed smart contracts (ERC-1271),
// and counterfactual smart contracts (ERC-6492) using a deployless verification approach.
const erc6492ByteCode = ""

// erc6492ABI is the ABI for the ERC-6492 Universal Signature Validator constructor.
const erc6492ABI = `[{
	"type": "constructor",
	"inputs": [
		{"name": "_signer", "type": "address", "internalType": "address"},
		{"name": "_hash", "type": "bytes32", "internalType": "bytes32"},
		{"name": "_signature", "type": "bytes", "internalType": "bytes"}
	],
	"stateMutability": "nonpayable"
}]`

// ParseErc6492SignatureResult represents the result of parsing an ERC-6492 signature.
type ParseErc6492SignatureResult struct {
	Address   *common.Address // create2Factory address (only present for ERC-6492 signatures)
	Data      []byte          // factoryCalldata (only present for ERC-6492 signatures)
	Signature []byte          // the actual signature to use
}

// ParseErc6492Signature parses a hex-formatted ERC-6492 flavored signature.
// If the signature is not in ERC-6492 format, then the underlying (original) signature is returned.
func ParseErc6492Signature(signature []byte) (*ParseErc6492SignatureResult, error) {
	if !isERC6492Signature(signature) {
		return &ParseErc6492SignatureResult{
			Signature: signature,
		}, nil
	}

	arguments, err := createERC6492Arguments()
	if err != nil {
		return nil, err
	}

	values, err := decodeERC6492Data(signature, arguments)
	if err != nil {
		return nil, err
	}

	return extractERC6492Components(values)
}

// createERC6492Arguments creates the ABI arguments for ERC-6492 decoding.
func createERC6492Arguments() (abi.Arguments, error) {
	addressType, err := abi.NewType("address", "", nil)
	if err != nil {
		return nil, fmt.Errorf("failed to create address type: %w", err)
	}

	bytesType, err := abi.NewType("bytes", "", nil)
	if err != nil {
		return nil, fmt.Errorf("failed to create bytes type: %w", err)
	}

	return abi.Arguments{
		{Type: addressType}, // create2Factory
		{Type: bytesType},   // factoryCalldata
		{Type: bytesType},   // originalSignature
	}, nil
}

// decodeERC6492Data attempts to decode ERC-6492 signature data.
func decodeERC6492Data(signature []byte, arguments abi.Arguments) ([]interface{}, error) {
	// Try to decode the signature directly (without removing magic value first)
	values, err := arguments.Unpack(signature)
	if err != nil {
		// If direct decode fails, try removing magic value as fallback
		return decodeWithoutMagic(signature, arguments)
	}

	return values, nil
}

// decodeWithoutMagic attempts to decode after removing the magic value.
func decodeWithoutMagic(signature []byte, arguments abi.Arguments) ([]interface{}, error) {
	if len(signature) < 32 {
		return nil, errors.New("signature too short for ERC-6492 format")
	}

	withoutMagic := signature[:len(signature)-32]
	values, err := arguments.Unpack(withoutMagic)
	if err != nil {
		return nil, fmt.Errorf("failed to decode ERC-6492 signature: %w", err)
	}

	return values, nil
}

// extractERC6492Components extracts and validates the decoded components.
func extractERC6492Components(values []interface{}) (*ParseErc6492SignatureResult, error) {
	if len(values) < 3 {
		return nil, fmt.Errorf("ERC-6492 decoded data has insufficient elements: got %d, expected 3", len(values))
	}

	create2Factory, ok := values[0].(common.Address)
	if !ok {
		return nil, fmt.Errorf("ERC-6492 create2Factory is not address type: %T", values[0])
	}

	factoryCalldata, ok := values[1].([]byte)
	if !ok {
		return nil, fmt.Errorf("ERC-6492 factoryCalldata is not bytes type: %T", values[1])
	}

	originalSignature, ok := values[2].([]byte)
	if !ok {
		return nil, fmt.Errorf("ERC-6492 originalSignature is not bytes type: %T", values[2])
	}

	return &ParseErc6492SignatureResult{
		Address:   &create2Factory,
		Data:      factoryCalldata,
		Signature: originalSignature,
	}, nil
}
